DROP TABLE IF EXISTS jpa.phones;
DROP TABLE IF EXISTS jpa.employees;
DROP TABLE IF EXISTS jpa.departments;
DROP TABLE IF EXISTS jpa.id_gen;
CREATE TABLE jpa.id_gen (GEN_NAME VARCHAR(80), GEN_VAL INT, CONSTRAINT pk_id_gen PRIMARY KEY (GEN_NAME));
CREATE TABLE jpa.departments (ID INT NOT NULL, DEPT_NAME VARCHAR(100), STATE VARCHAR(100), CITY VARCHAR(100), STREET VARCHAR(1000), PRIMARY KEY (ID));
CREATE TABLE jpa.employees (ID INT NOT NULL, EMP_NAME VARCHAR(100), SALARY INT, DEPARTMENT_ID INT, DOB DATE, STATE VARCHAR(100), CITY VARCHAR(100), STREET VARCHAR(1000), PRIMARY KEY (ID), CONSTRAINT FK_DEPARTMENTS FOREIGN KEY (DEPARTMENT_ID) REFERENCES departments(id));
CREATE TABLE jpa.phones (PHONE_TYPE VARCHAR(10), PHONE_NUMBER VARCHAR(20), EMP_ID INT, PRIMARY KEY (PHONE_TYPE, EMP_ID), CONSTRAINT FK_EMPLOYEES FOREIGN KEY (EMP_ID) REFERENCES employees(id));